import { useEffect } from 'react';

const createGrid = (paper) => {  
    const cellSize = 20;
    const gridSizeX = paper.view.bounds.width;
    const gridSizeY = paper.view.bounds.height;

    const startPointX = paper.view.bounds.left;
    const startPointY = paper.view.bounds.top;

    // Create vertical Lines
    for(let y = startPointY; y <= gridSizeY; y += cellSize ){
        const start = new paper.Point(startPointX, y);
        const end = new paper.Point(gridSizeX, y);
        new paper.Path.Line({
            from: start,
            to: end,
            strokeColor: new paper.Color(0, 0, 0, 0.2),
            strokeWidth: 0.5,
            strokeDashArray: [4,4],
            name: 'gridDot'
        });
    }

    // Creeate horizontal Lines
    for(let x = startPointX; x <= gridSizeX; x += cellSize ){
        const start = new paper.Point(x, startPointY);
        const end = new paper.Point(x, gridSizeY);
        new paper.Path.line({
            from: start,
            to: end,
            strokeColor: new paper.Color(0, 0, 0, 0.2),
            strokeWidth: 0.5,
            strokeDashArray: [4,4],
            name: 'gridDot'
        });
    }
};

const createYarnLine = (paper) => {
    const canvasWidth = paper.view.bounds.width;
    const canvasHeight = paper.view.bounds.height;
    const centerY = canvasHeight / 2; // Middle of the canvas on the Y-axis

    const amplitude = 20;  // The height of the wave
    const frequency = 50;  // Distance between peaks
    const strokeColor = 'black';
    const strokeWidth = 2;

    // Create the path for the yarn
    const yarnPath = new paper.Path({
        strokeColor: strokeColor,
        strokeWidth: strokeWidth,
        strokeCap: 'round'
    });

    // Loop through points to create a wavy line
    for (let x = 0; x <= canvasWidth; x += frequency) {
         const offsetY = Math.sin(x / frequency * Math.PI) * amplitude; // Wavy effect
        const point = new paper.Point(x, centerY + offsetY);
        yarnPath.add(point);  // Add each point to the path
    }

    yarnPath.smooth();  
};


const DirectManipulation = ({ paper, paperReady }) => {
    useEffect(() => {
        if (paperReady) {
            const paper = paperReady;
            paper.project.clear();
            paper.view.zoom = 10;

            createGrid(paper);
            createYarnLine(paper);
        }
    }, [paperReady]);

    return null;
};

export { DirectManipulation };
