import React, { useState } from 'react';

const InputMenu = ({ onProcessInput }) => {
    const [userInput, setUserInput] = useState('');   // To store user input

    // Handle input field changes
    const handleInputChange = (event) => {
        setUserInput(event.target.value);  // Update user input state
    };

    // Handle submit action (e.g., Download or any other process)
    const handleSubmit = () => {
        if (userInput.trim() === "") {
            alert("Please enter a valid input.");
            return;
        }
        onProcessInput(userInput);  // Call the function passed via props
    };

    return (
        <div className="input-menu">
            <h3>Enter Your String</h3>
            <input 
                type="text" 
                placeholder="Enter a string" 
                value={userInput} 
                onChange={handleInputChange}  // Capture user input
                style={{ padding: '8px', width: '100%', marginBottom: '10px' }}
            />
            <button onClick={handleSubmit} style={{ padding: '10px', cursor: 'pointer' }}>
                Submit
            </button>
        </div>
    );
};

export default InputMenu;
