const createMenu = (setMenus, paperCanvasRef, menus) => {
    // Create a new menu object with a unique ID and default position
    const newMenu = {
        id: menus.length + 1, // Unique ID based on the number of menus
        position: { x: 100 + menus.length * 20, y: 100 + menus.length * 20 }, // Slight offset for each new menu
    };

    // Update the state to include the new menu
    setMenus((prevMenus) => [...prevMenus, newMenu]);
};

export default createMenu;
