import './App.css';
import 'semantic-ui-css/semantic.min.css';
import { useState, useRef } from 'react';
import { PaperCanvas } from './components/PaperCanvas.jsx';
import { View } from './components/View.jsx';
import { CreateGridBG } from './interactions/CreateGridBG.jsx';
import { Menus } from './components/Menus.jsx';
import Draggable from 'react-draggable';

function App() {
  const [appScreen, setScreen] = useState("paper");
  const [paperReady, setPaperReady] = useState(false);
  const [menus, setMenus] = useState([]);
  const paperCanvasRef = useRef(null);

  const MAX_MENUS = 12; // Max number of draggable menus

  const addNewMenu = () => {
    if (menus.length >= MAX_MENUS) {
      alert("Maximum of 12 menus reached");
      return;
    }

    const paperCanvas = paperCanvasRef.current;
    if (paperCanvas) {
      const canvasRect = paperCanvas.getBoundingClientRect();
      const centerX = canvasRect.width / 2 - 150; // Center the menu horizontally
      const centerY = canvasRect.height / 2 - 150; // Center the menu vertically

      const newMenu = { 
        id: Date.now(), 
        position: { x: centerX, y: centerY }
      };

      setMenus([...menus, newMenu]); // Add new menu
    }
  };

  const removeMenu = (id) => {
    setMenus(menus.filter(menu => menu.id !== id)); // Remove menu by ID
  };

  return (
    <div className="App">
      {appScreen === "paper" && (
        <View icon="" goToScreen="" screen={appScreen} setScreen={setScreen}>
          <div ref={paperCanvasRef} style={{ position: 'relative', width: '100%', height: '500px', backgroundColor: '#f5f5f5' }}>
            <PaperCanvas setPaperReady={setPaperReady} />
          </div>
          {paperReady && (
            <CreateGridBG 
              paper={paperReady} 
              paperReady={paperReady}
            />
          )}
          <button onClick={addNewMenu}>Add New Menu</button>
          {menus.map((menu) => (
            <Draggable key={menu.id} defaultPosition={menu.position}>
              <div className="draggable-menu" style={{ position: 'absolute', zIndex: 10, backgroundColor: '#fff', padding: '20px', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0,0,0,0.2)' }}>
                <Menus />
                <button onClick={() => removeMenu(menu.id)}>Close Menu</button>
              </div>
            </Draggable>
          ))}
        </View>
      )}
    </div>
  );
}

export default App;
