const createMenu = (setMenus, paperCanvasRef, menus) => {
    const paperCanvas = paperCanvasRef.current;
    if (paperCanvas) {
      const canvasRect = paperCanvas.getBoundingClientRect();
      const centerX = canvasRect.width / 2 - 150;
      const centerY = canvasRect.height / 2 - 150;

      const newMenu = { 
        id: Date.now(), 
        position: { x: centerX, y: centerY }
      };

      setMenus([...menus, newMenu]); // Add new menu to the state
    }
  };

  export default createMenu;
