import React, { useState, useEffect } from 'react';
import { Menus } from './components/Menus.jsx';
import Draggable from 'react-draggable';
import './App.css'; // Import CSS for styling

const START_SCREEN = "paper";

function App() {
  const [appScreen, setScreen] = useState(START_SCREEN);
  const [paperReady, setPaperReady] = useState(false);
  const [file, setFile] = useState(null);
  const [svgContent, setSvgContent] = useState(null); // State for SVG content
  const [menuPosition, setMenuPosition] = useState({ x: 0, y: 0 }); // State for menu position

  useEffect(() => {
    // Calculate the initial center position of the menu
    const initialX = window.innerWidth / 2 - 150; // 150 is half the width of the menu
    const initialY = window.innerHeight / 2 - 100; // Adjust based on your menu's height
    setMenuPosition({ x: initialX, y: initialY });
  }, []);

  // File upload handling logic from FileUpload component
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append('file', file);

    const response = await fetch('http://127.0.0.1:8000/plot-logs/', { 
        method: 'POST',
        body: formData,
    });        

    if (response.ok) {
        console.log('File uploaded successfully');
        const svg = await response.text();
        setSvgContent(svg);  // Set the SVG content to display in UI
    } else {
        console.error('File upload failed');
        const errorText = await response.text();
        console.error('Response Text:', errorText);
    }
  };

  return (
    <div className="App">
      <div className="paper-container">
        {/* Paper canvas goes here */}
        <div className="paper-canvas">
          {/* Assuming you want to use an SVG or canvas for the paper area */}
          <svg width="100%" height="100%">
            {/* Example of paper content */}
            <rect width="100%" height="100%" fill="#fafafa" />
            {svgContent && <g dangerouslySetInnerHTML={{ __html: svgContent }} />}
          </svg>
        </div>

        {/* Draggable Menu on top of canvas */}
        <Draggable defaultPosition={{ x: menuPosition.x, y: menuPosition.y }}>
          <div className="draggable-menu">
            <Menus />
          </div>
        </Draggable>
      </div>
    </div>
  );
}

export default App;
