import './App.css';
import 'semantic-ui-css/semantic.min.css';
import { useState, useRef, useEffect } from 'react';
import { PaperCanvas } from './components/PaperCanvas.jsx';
import { View } from './components/View.jsx';
import { CreateGridBG } from './interactions/CreateGridBG.jsx';
import { Menus } from './components/Menus.jsx';
import Draggable from 'react-draggable';

const START_SCREEN = "paper";

function App() {
  const [appScreen, setScreen] = useState(START_SCREEN);
  const [paperReady, setPaperReady] = useState(false);
  const [file, setFile] = useState(null);
  const [svgContent, setSvgContent] = useState(null); // State for SVG content
  const [menus, setMenus] = useState([]); // Array to track all menus
  const paperCanvasRef = useRef(null); // Ref for PaperCanvas

  // Function to add a new menu and position it in the center of the PaperCanvas
  const addNewMenu = () => {
    const paperCanvas = paperCanvasRef.current;
    if (paperCanvas) {
      const canvasRect = paperCanvas.getBoundingClientRect();

      // Calculate the center position of PaperCanvas
      const centerX = canvasRect.width / 2 - 150; // Assuming menu width is 300px
      const centerY = canvasRect.height / 2 - 150; // Assuming menu height is 300px

      const newMenu = { 
        id: Date.now(), 
        position: { 
          x: canvasRect.left + centerX, // Adjust relative to PaperCanvas
          y: canvasRect.top + centerY   // Adjust relative to PaperCanvas
        }
      };

      setMenus([...menus, newMenu]);
    }
  };

  // Function to remove a menu by its ID
  const removeMenu = (id) => {
    setMenus(menus.filter(menu => menu.id !== id));
  };

  // File upload handling logic from FileUpload component
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append('file', file);

    const response = await fetch('http://127.0.0.1:8000/plot-logs/', { 
        method: 'POST',
        body: formData,
    });        

    if (response.ok) {
        console.log('File uploaded successfully');
        const svg = await response.text();
        setSvgContent(svg);  // Set the SVG content to display in UI
    } else {
        console.error('File upload failed');
        const errorText = await response.text();
        console.error('Response Text:', errorText);
    }
  };

  const handleOtherPlot = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append('file', file);

    const response = await fetch('http://127.0.0.1:8000/plot-other-log/', { 
        method: 'POST',
        body: formData,
    });        

    if (response.ok) {
        console.log('File uploaded successfully');
        const svg = await response.text();
        setSvgContent(svg);  // Set the SVG content to display in UI
    } else {
        console.error('File upload failed');
        const errorText = await response.text();
        console.error('Response Text:', errorText);
    }
  };

  return (
    <div className="App">
      {appScreen === "paper" && (
        <View icon="" goToScreen="" screen={appScreen} setScreen={setScreen}>
          <div ref={paperCanvasRef} style={{ position: 'relative' }}>
            <PaperCanvas setPaperReady={setPaperReady} />
          </div>
          {paperReady && (
            <CreateGridBG 
              paper={paperReady} 
              paperReady={paperReady}
            />
          )}

          {/* Button to add new menu */}
          <button onClick={addNewMenu}>Add New Menu</button>

          {/* Render all draggable menus */}
          {menus.map((menu) => (
            <Draggable 
              key={menu.id} 
              defaultPosition={menu.position} // Set initial position to the center of PaperCanvas
            >
              <div className="draggable-menu" style={{ position: 'absolute', zIndex: 10, backgroundColor: '#fff', padding: '20px', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0,0,0,0.2)' }}>
                <Menus 
                  handleFileChange={handleFileChange} 
                  handleSubmit={handleSubmit} 
                  handleOtherPlot={handleOtherPlot}
                  file={file} 
                  svgContent={svgContent}
                />
                <button onClick={() => removeMenu(menu.id)}>Close Menu</button>
              </div>
            </Draggable>
          ))}
        </View>
      )}
    </div>
  );
}

export default App;
