import './App.css';
import 'semantic-ui-css/semantic.min.css';
import { useState } from 'react';
import { PaperCanvas } from './components/PaperCanvas.jsx';
import { View } from './components/View.jsx';
import { CreateGridBG } from './interactions/CreateGridBG.jsx';
import Draggable from 'react-draggable';
import FileUpload from './interactions/UploadFile.jsx'; // Import the FileUpload component

const START_SCREEN = "paper";

function App() {
  const [appScreen, setScreen] = useState(START_SCREEN);
  const [paperReady, setPaperReady] = useState(false);

  return (
    <div className="App">
      {appScreen === "paper" && (
        <View icon="" goToScreen="" screen={appScreen} setScreen={setScreen}>
          <PaperCanvas setPaperReady={setPaperReady} />
          {paperReady && (
            <CreateGridBG 
              paper={paperReady} 
              paperReady={paperReady}
            />
          )}
          {/* Draggable Menu */}
          <Draggable>
            <div 
              className="draggable-menu" 
              style={{
                position: 'absolute', 
                zIndex: 10, 
                backgroundColor: '#fff', 
                padding: '20px', 
                borderRadius: '8px', 
                boxShadow: '0 4px 8px rgba(0,0,0,0.2)'
              }}
            >
              {/* Rendering the FileUpload component inside the draggable menu */}
              <FileUpload /> 
            </div>
          </Draggable>
        </View>
      )}
    </div>
  );
}

export default App;
