// createMenu.jsx
export default function createMenu(setMenus, paperCanvasRef, menus) {
    const newMenu = {
      id: menus.length + 1, // Assign unique id based on length of menus array
      position: { x: 100, y: 100 }, // Default position for the menu
    };

    // Add the new menu to the menus state
    setMenus((prevMenus) => [...prevMenus, newMenu]);
  }
