import React, { useState } from 'react';

const InputMenu = () => {
    const [input1, setInput1] = useState('');   // Store input 1
    const [input2, setInput2] = useState('');   // Store input 2
    const [input3, setInput3] = useState('');   // Store input 3
    const [image, setImage] = useState(null);   // Store the image URL

    // Handle input field changes
    const handleInput1Change = (event) => {
        setInput1(event.target.value);  // Update input 1 state
    };

    const handleInput2Change = (event) => {
        setInput2(event.target.value);  // Update input 2 state
    };

    const handleInput3Change = (event) => {
        setInput3(event.target.value);  // Update input 3 state
    };

    // Handle submit action (demo version)
    const handleSubmit = () => {
        if (input1.trim() === "" || input2.trim() === "" || input3.trim() === "") {
            alert("Please enter valid inputs.");
            return;
        }

        // Simulate success and display a predefined image
        setImage('https://via.placeholder.com/300');  // Replace this with your predetermined image URL

        alert("Demo: Processing successful. Showing predetermined image.");
    };

    return (
        <div className="input-menu">
            <h3>Enter Your Strings</h3>
            <input 
                type="text" 
                placeholder="Contract Address" 
                value={input1} 
                onChange={handleInput1Change}  
                style={{ padding: '8px', width: '100%', marginBottom: '10px' }}
            />
            <input 
                type="text" 
                placeholder="IPFS Hash" 
                value={input2} 
                onChange={handleInput2Change}  
                style={{ padding: '8px', width: '100%', marginBottom: '10px' }}
            />
            <input 
                type="text" 
                placeholder="Ethereum Address" 
                value={input3} 
                onChange={handleInput3Change}  
                style={{ padding: '8px', width: '100%', marginBottom: '10px' }}
            />
            <button onClick={handleSubmit} style={{ padding: '10px', cursor: 'pointer' }}>
                Submit
            </button>

            {/* Display the image if available */}
            {image && (
                <div style={{ marginTop: '20px' }}>
                    <img src={image} alt="Demo Result" style={{ maxWidth: '100%' }} />
                </div>
            )}
        </div>
    );
};

export default InputMenu;
