import { useEffect } from 'react';

const createGrid = (paper) => {
    console.log('Creating grid...');
    const cellSize = 20;
    const gridSizeX = paper.view.bounds.width;
    const gridSizeY = paper.view.bounds.height;

    // Loop through the grid and place a small dot (circle) at each intersection
    for (let y = 0; y <= gridSizeY; y += cellSize) {
        for (let x = 0; x <= gridSizeX; x += cellSize) {
            // Create a circle at each grid intersection
            new paper.Path.Circle({
            center: new paper.Point(x, y),
            radius: 1.5,
            fillColor: new paper.Color(0, 0, 0, 0.1),
            name: 'gridDot',
            });
        }
    }
};

const createYarnLine = (paper) => {
    const canvasWidth = paper.view.bounds.width;
    const canvasHeight = paper.view.bounds.height;
    const centerX = canvasWidth / 2; // Middle of the canvas on the X-axis

    const amplitude = 10;  // The width of the wave (horizontal displacement)
    const frequency = 11;  // Distance between peaks (vertical distance)
    const strokeColor = 'black';
    const strokeWidth = 2;

    // Create the path for the yarn
    const yarnPath = new paper.Path({
        strokeColor: strokeColor,
        strokeWidth: strokeWidth,
        strokeCap: 'round'
    });

    // Loop through points to create a wavy vertical line
    for (let y = 0; y <= canvasHeight; y += amplitude) {
        const offsetX = Math.sin(y / frequency * Math.PI) * amplitude; // Wavy effect for X-axis
        const point = new paper.Point(centerX + offsetX, y); // X affected by wave, Y is linear
        yarnPath.add(point);  // Add each point to the path
    }

    yarnPath.smooth();  
};



const DirectManipulation = ({ paper, paperReady }) => {
    useEffect(() => {
        if (paperReady) {
            createGrid(paper);
            createYarnLine(paper);
        }
    }, [paperReady]);

    return null;
};

export { DirectManipulation };
