import './App.css';
import 'semantic-ui-css/semantic.min.css';
import { useState } from 'react';
import { PaperCanvas } from './components/PaperCanvas.jsx';
import { View } from './components/View.jsx';
import { CreateGridBG } from './interactions/CreateGridBG.jsx';
import Draggable from 'react-draggable';
import InputMenu from './components/InputMenu'; // Importing InputMenu component

const START_SCREEN = "paper";

function App() {
  const [appScreen, setScreen] = useState(START_SCREEN);
  const [paperReady, setPaperReady] = useState(false);
  const [processedInput, setProcessedInput] = useState(null); // Store the processed user input

  // Function to process the user input from InputMenu
  const handleProcessInput = (input) => {
      console.log('Processing user input:', input);
      setProcessedInput(input);  // Save the processed input (you can add your logic here)
  };

  return (
    <div className="App">
      {appScreen === "paper" && (
        <View icon="" goToScreen="" screen={appScreen} setScreen={setScreen}>
          <PaperCanvas setPaperReady={setPaperReady} />
          {paperReady && (
            <CreateGridBG 
              paper={paperReady} 
              paperReady={paperReady}
            />
          )}
          {/* Draggable Menu */}
          <Draggable>
            <div 
              className="draggable-menu" 
              style={{
                position: 'absolute', 
                zIndex: 10, 
                backgroundColor: '#fff', 
                padding: '20px', 
                borderRadius: '8px', 
                boxShadow: '0 4px 8px rgba(0,0,0,0.2)'
              }}
            >
              {/* Render the InputMenu component and pass down the handler */}
              <InputMenu onProcessInput={handleProcessInput} />
            </div>
          </Draggable>

          {/* Optionally display the processed input */}
          {processedInput && (
            <div style={{ marginTop: '20px', border: '1px solid #ddd', padding: '10px' }}>
              <h4>Processed Input:</h4>
              <p>{processedInput}</p>
            </div>
          )}
        </View>
      )}
    </div>
  );
}

export default App;
